void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float t = iTime * 0.5;
    float d = length(uv);
    float a = atan(uv.y, uv.x);
    float wave = sin(d * 8.0 - t * 4.0) * 0.5 + 0.5;
    float rings = sin(d * 12.0 - t * 2.0) * 0.5 + 0.5;
    vec3 col = 0.5 + 0.5 * cos(6.28318 * (wave + vec3(0.0, 0.33, 0.67)));
    col *= smoothstep(0.6, 0.4, d);
    fragColor = vec4(col, 1.0);
}

